          //       />
          //       <Text>{item.name}</Text>
          //     </View>
          //   </TouchableOpacity>
          // ))
        }

          <FlatList 
          numColumns={2}
          style={styles.container}
          data={allPok}
          renderItem={renderItem}
          keyExtractor={(item) => `key-${item.species.name}`}
          />
      </SafeAreaView>
    </Provider>
  );
}

