import React, {useEffect, useState, useReducer} from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet,
  Text,
  View,
  TouchableOpacity,
  TextInput,
  FlatList,
  Image
} from 'react-native';
import { getAllPok } from './src/services/getAPI';
import { Provider } from 'react-redux';
import store from './src/store/store';
import pokemon from './src/reducers/getPokByName.reducer';
import { SafeAreaView } from 'react-native-web';

export default function App() {
  const [state, dispatch] = useReducer(pokemon);

  // console.log(state);

  const [allPok, setAllPok] = useState([]);

  useEffect( async () => {
    await getAllPok(setAllPok);
  }, []);

  {console.log(allPok)}

  const renderItem = ({item, index}) => {
    return (
      <View style={styles.item}>
        {

        }

        <Image
          style={styles.image}
          resizeMode='contain'
          source={item.sprites.back_default}
        />
        <Text>{item.sprites.back_default}</Text>
      </View>
    )
  }

  return (
    <Provider store={ store }>
      <SafeAreaView >
        {
          allPok.map((item) => (
            <TouchableOpacity key={item.id} style={styles.container}>
              <View style={styles.item}>
                <Image
                  style={{width: 150, height: 150}}
                  source={item.sprites.other.home.front_default}
                />
                <Text>{item.name}</Text>
              </View>
            </TouchableOpacity>
          ))
        }

          {/* <FlatList 
