import React, {useEffect, useState, useReducer} from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet,
  Text,
  View,
  TouchableOpacity,
  TextInput,
  FlatList,
  Image
} from 'react-native';
import { getAllPok } from './src/services/getAPI';
import { Provider } from 'react-redux';
import store from './src/store/store';
import pokemon from './src/reducers/getPokByName.reducer';
import { SafeAreaView } from 'react-native-web';

export default function App() {
  const [state, dispatch] = useReducer(pokemon);

  console.log(state);

  const [allPok, setAllPok] = useState([]);

  useEffect( async () => {
    await getAllPok(setAllPok);
  }, []);

  {console.log(allPok[0].name)}

  const renderItem = ({item, index}) => {
    return (
      <View style={styles.item}>
        <Image
          style={styles.image}
          resizeMode='contain'
          source={item.sprites.back_default}
        />
        <Text>{item.species.name}</Text>
      </View>
    )

  }

  return (
    <Provider store={ store }>
      <SafeAreaView>
        <FlatList 
          numColumns={2}
          style={styles.container}
          data={allPok}
          renderItem={renderItem}
          keyExtractor={(item) => `key-${item.species.name}`}
        />
      </SafeAreaView>
      {/* <View style={styles.container}>
